def draw_slide():
    fill(0, 0, 767, 511, 0)
    i, x1, y1 = 0, 0, 0
    while i < len(dat):
        p = dat[i]
        if type(p) is str:
            if p == 'mv':
                x1, y1 = dat[i + 1], dat[i + 2]
                i += 2
            elif p == 'pa':
                paint(dat[i + 1], dat[i + 2], dat[i + 3])
                i += 3
        else:
            if x1 is not None:
                line(x1, y1, dat[i], dat[i + 1], 3)
                x1 = None
            else:
                line_to(dat[i], dat[i + 1], 3)
            i += 1
        i += 1

